-- viết function cập nhật lớp trưởng

DECLARE @MyCursor CURSOR
DECLARE @TenCN int
DECLARE @tenDV int
DECLARE @i int =0
SET @MyCursor = CURSOR FAST_FORWARD
FOR
select TaiKhoan.id_ChuyenNganh, TaiKhoan.id_DonVi from TaiKhoan, ChuyenNganh, DonVi 
where TaiKhoan.id_ChuyenNganh = ChuyenNganh.id_ChuyenNganh and TaiKhoan.id_DonVi  = DonVi.id_DonVi
group by TaiKhoan.id_ChuyenNganh, TaiKhoan.id_DonVi
OPEN @MyCursor 
FETCH NEXT FROM @MyCursor
INTO @tenCN , @tenDV
WHILE @@FETCH_STATUS = 0
BEGIN
	DECLARE @id_TK varchar(200)
	select @id_TK=id_TaiKhoan from TaiKhoan where id_ChuyenNganh =@TenCN and id_DonVi = @tenDV
	update TaiKhoan set id_ChucVu =2 where id_TaiKhoan = @id_TK
	

FETCH NEXT FROM @MyCursor
INTO @tenCN , @tenDV
END
CLOSE @MyCursor
DEALLOCATE @MyCursor

